INSERT INTO `homepage_content` (`id`, `service_body`, `feature_body`)
VALUES
	(1,'<div class=\"row\">\n<div class=\"col-md-4\">\n<div class=\"icon icon-landlords\"> </div>\n\n<div class=\"service-block\">\n<h2><a href=\"/landlord-features\">Landlords</a></h2>\n\n<p>Have the abillity to manage all of your properties with pay-as-you go services and more!</p>\n</div>\n</div>\n\n<div class=\"col-md-4\">\n<div class=\"icon icon-tenants\"> </div>\n\n<div class=\"service-block\">\n<h2><a href=\"/tenant-features\">Tenants</a></h2>\n\n<p>Pay your rent online and submit service requests to your landlords.</p>\n</div>\n</div>\n\n<div class=\"col-md-4\">\n<div class=\"icon icon-property\"> </div>\n\n<div class=\"service-block\">\n<h2><a href=\"/property/available\">Available Properties</a></h2>\n\n<p>Search available listing and apply for rentals on-line.</p>\n</div>\n</div>\n</div>','<div class=\"features-title-block\">\n            <span>eRentPay Portal Features</span>\n        </div>\n        <div class=\"row text-capabilities\">\n            <div class=\"col-md-6\">\n                <h2>Manager Capabilities:</h2>\n\n                <div class=\"block-service\">\n                    <div class=\"col-md-2\"><div class=\"landlord1-icon icon\"></div></div>\n                    <div class=\"col-md-10 text-block-service\">\n                        <h3>Post Available Listings</h3>\n\n                        <p>Post your available listings through ERentPay and it will automatically post to other sites\n                            site such as </p>\n                    </div>\n                </div>\n                <div class=\"block-service\">\n                    <div class=\"col-md-2\"><div class=\"landlord2-icon icon\"></div></div>\n                    <div class=\"col-md-10 text-block-service\">\n                        <h3>Create, View and Sign Applications &amp; Forms</h3>\n\n                        <p>With everything at your fingertips you can now create, view and have your tenant sign any\n                            forms and applications on ERentPay. Everything all in one ERentPay account!</p>\n                    </div>\n                </div>\n                <div class=\"block-service\">\n                    <div class=\"col-md-2\"><div class=\"landlord3-icon icon\"></div></div>\n                    <div class=\"col-md-10 text-block-service\">\n                        <h3>Credit &amp; Background Checks</h3>\n\n                        <p>ERentPay allows you to perform your Background and Credit Checks on any prospective tenant.\n                            With ERentPay’s pay-as-you-go service, you pay only for what you use and can manage\n                            everything all on your own!</p>\n                    </div>\n                </div>\n            </div>\n\n            <div class=\"col-md-6\">\n                <h2>Tenant Capabilities:</h2>\n\n                <div class=\"block-service\">\n                    <div class=\"col-md-2\"><div class=\"tenant1-icon icon\"></div></div>\n                    <div class=\"col-md-10 text-block-service\">\n                        <h3>Pay Your Rent</h3>\n\n                        <p>Save money when paying your rent online. ERentPay allows for ACH or credit card payments\n                            through its automated system. There is also an option for reoccurring payments that save\n                            time and money. </p>\n                    </div>\n                </div>\n                <div class=\"block-service\">\n                    <div class=\"col-md-2\"><div class=\"tenant2-icon icon\"></div></div>\n                    <div class=\"col-md-10 text-block-service\">\n                        <h3>Submit Service Requests</h3>\n\n                        <p>Submit Maintenance and Service Requests online to quickly resolve any rental unit issues you\n                            may be dealing with. This easy to use system allows you to immediately report any issue and\n                            get a response in a timely manner. It also allows for a tracking system of past maintenance\n                            issues related to your rental.</p>\n                    </div>\n                </div>\n                <div class=\"block-service\">\n                    <div class=\"col-md-2\"><div class=\"tenant3-icon icon\"></div></div>\n                    <div class=\"col-md-10 text-block-service\">\n                        <h3>Access Rental Documentation</h3>\n\n                        <p>Have complete online access to all Rental documentation you have signed. This allows you to\n                            not only view your rental applications and contracts, but also sign them online and check\n                            their status quickly.</p>\n                    </div>\n                </div>\n            </div>\n        </div>');
